"""
EOF v1 validation code
"""

import pytest
from ethereum_test_tools import EOFTestFiller
from ethereum_test_tools import EOFException, Opcodes as Op, UndefinedOpcodes as UOp
from ethereum_test_tools.eof.v1 import Container, ContainerKind, Section

@pytest.mark.parametrize(
    "eof_code,expected_hex_bytecode,exception",
    [
        
        pytest.param(
                Container(
                    name="EOF1V00001",
                    sections=[
                        Section.Code(code=Op.CALLF[0x0001] + Op.STOP, max_stack_height=0),
						Section.Code(code=Op.PUSH1[0x01] * 1023 + Op.POP * 1023 + Op.RETF, code_outputs=0, max_stack_height=1023),
                    ],
                    kind=ContainerKind.RUNTIME,
                ),
                "",
                None,
                id="valid_max_stack_height_1023_second_code_section",
            ),
                
        pytest.param(
                Container(
                    name="EOF1V00002",
                    sections=[
                        Section.Code(code=Op.PUSH1[0x01] * 1023 + Op.POP * 1023 + Op.CALLF[0x0001] + Op.STOP, max_stack_height=1023),
						Section.Code(code=Op.RETF, code_outputs=0, max_stack_height=0),
                    ],
                    kind=ContainerKind.RUNTIME,
                ),
                "",
                None,
                id="valid_max_stack_height_1023_first_code_section",
            ),
                
        pytest.param(
                Container(
                    name="EOF1V00003",
                    sections=[
                        Section.Code(code=Op.PUSH1[0x01] * 1024 + Op.STOP, max_stack_height=1024),
                    ],
                    kind=ContainerKind.RUNTIME,
                ),
                "",
                EOFException.MAX_STACK_HEIGHT_ABOVE_LIMIT,
                id="invalid_max_stack_height_1024_a",
            ),
                
        pytest.param(
                Container(
                    name="EOF1V00004",
                    sections=[
                        Section.Code(code=Op.PUSH1[0x01] * 1024 + Op.CALLF[0x0001] + Op.POP * 1024 + Op.STOP, max_stack_height=1024),
						Section.Code(code=Op.RETF, code_outputs=0, max_stack_height=0),
                    ],
                    kind=ContainerKind.RUNTIME,
                ),
                "",
                EOFException.MAX_STACK_HEIGHT_ABOVE_LIMIT,
                id="invalid_max_stack_height_1024_b",
            ),
                
        pytest.param(
                Container(
                    name="EOF1V00005",
                    sections=[
                        Section.Code(code=Op.CALLF[0x0001] + Op.STOP, max_stack_height=0),
						Section.Code(code=Op.PUSH1[0x01] * 1024 + Op.POP * 1024 + Op.RETF, code_outputs=0, max_stack_height=1023),
                    ],
                    kind=ContainerKind.RUNTIME,
                ),
                "",
                EOFException.INVALID_MAX_STACK_HEIGHT,
                id="invalid_real_max_stack_height_1024_second_code_section",
            ),
                
        pytest.param(
                Container(
                    name="EOF1V00006",
                    sections=[
                        Section.Code(code=Op.PUSH1[0x01] * 1024 + Op.CALLF[0x0001] + Op.POP * 1024 + Op.STOP, max_stack_height=1023),
						Section.Code(code=Op.RETF, code_outputs=0, max_stack_height=0),
                    ],
                    kind=ContainerKind.RUNTIME,
                ),
                "",
                EOFException.INVALID_MAX_STACK_HEIGHT,
                id="invalid_real_max_stack_height_1024_first_code_section",
            ),
                
        pytest.param(
                Container(
                    name="EOF1V00007",
                    sections=[
                        Section.Code(code=Op.PUSH1[0x00] + Op.RJUMPI[0x0002] + Op.PUSH1[0x01] + Op.STOP, max_stack_height=1),
                    ],
                    kind=ContainerKind.RUNTIME,
                ),
                "0xef0001010004020001000804000000008000016000e10002600100",
                None,
                id="valid_rjumpi_stack_height",
            ),
                
        pytest.param(
                Container(
                    name="EOF1V00008",
                    sections=[
                        Section.Code(code=Op.PUSH1[0x00] + Op.RJUMPI[0xfffd] + Op.STOP, max_stack_height=1),
                    ],
                    kind=ContainerKind.RUNTIME,
                ),
                "0xef0001010004020001000604000000008000016000e1fffd00",
                EOFException.STACK_HEIGHT_MISMATCH,
                id="invalid_rjumpi_stack_height_mismatch_a",
            ),
                
        pytest.param(
                Container(
                    name="EOF1V00009",
                    sections=[
                        Section.Code(code=Op.PUSH1[0x00] + Op.RJUMPV[-4] + Op.STOP, max_stack_height=1),
                    ],
                    kind=ContainerKind.RUNTIME,
                ),
                "0xef0001010004020001000704000000008000016000e200fffc00",
                EOFException.STACK_HEIGHT_MISMATCH,
                id="invalid_rjumpv_stack_height_mismatch_b",
            ),
                
    ]
)

def test_example_valid_invalid(
    eof_test: EOFTestFiller,
    eof_code: Container,
    expected_hex_bytecode: str,
    exception: EOFException | None,
):
    """
    Verify eof container construction and exception
    """
    if expected_hex_bytecode[0:2] == "0x":
        expected_hex_bytecode = expected_hex_bytecode[2:]
    assert bytes(eof_code) == bytes.fromhex(expected_hex_bytecode)

    eof_test(
        data=eof_code,
        expect_exception=exception,
    )
