"""
EOF v1 validation code
"""

import pytest
from ethereum_test_tools import EOFTestFiller
from ethereum_test_tools import EOFException, Opcodes as Op, UndefinedOpcodes as UOp
from ethereum_test_tools.eof.v1 import Container, ContainerKind, Section

@pytest.mark.parametrize(
    "eof_code,expected_hex_bytecode,exception",
    [
        
        pytest.param(
                Container(
                    name="EOF1V00001",
                    sections=[
                        Section.Code(code=Op.PUSH0 + Op.PUSH1[0x00] + Op.RJUMPI[0x0002] + Op.PUSH0 * 511 + Op.JUMPF[0x0000], max_stack_height=512),
                    ],
                    kind=ContainerKind.RUNTIME,
                ),
                "",
                None,
                id="jumpf_stack_overflow_variable_stack_1",
            ),
                
        pytest.param(
                Container(
                    name="EOF1V00002",
                    sections=[
                        Section.Code(code=Op.PUSH0 + Op.PUSH1[0x00] + Op.RJUMPI[0x0002] + Op.PUSH0 * 511 + Op.JUMPF[0x0001], max_stack_height=512),
						Section.Code(code=Op.PUSH0 * 515 + Op.STOP, max_stack_height=515),
                    ],
                    kind=ContainerKind.RUNTIME,
                ),
                "",
                EOFException.STACK_OVERFLOW,
                id="jumpf_stack_overflow_variable_stack_2",
            ),
                
        pytest.param(
                Container(
                    name="EOF1V00003",
                    sections=[
                        Section.Code(code=Op.PUSH0 + Op.PUSH1[0x00] + Op.RJUMPI[0x0002] + Op.PUSH0 * 511 + Op.JUMPF[0x0001], max_stack_height=512),
						Section.Code(code=Op.PUSH0 * 514 + Op.STOP, max_stack_height=514),
                    ],
                    kind=ContainerKind.RUNTIME,
                ),
                "",
                EOFException.STACK_OVERFLOW,
                id="jumpf_stack_overflow_variable_stack_3",
            ),
                
        pytest.param(
                Container(
                    name="EOF1V00004",
                    sections=[
                        Section.Code(code=Op.PUSH0 + Op.PUSH1[0x00] + Op.RJUMPI[0x0002] + Op.PUSH0 * 1022 + Op.JUMPF[0x0001], max_stack_height=1023),
						Section.Code(code=Op.PUSH0 + Op.STOP, max_stack_height=1),
                    ],
                    kind=ContainerKind.RUNTIME,
                ),
                "",
                None,
                id="jumpf_stack_overflow_variable_stack_4",
            ),
                
        pytest.param(
                Container(
                    name="EOF1V00005",
                    sections=[
                        Section.Code(code=Op.PUSH0 + Op.PUSH1[0x00] + Op.RJUMPI[0x0002] + Op.PUSH0 * 1022 + Op.JUMPF[0x0001], max_stack_height=1023),
						Section.Code(code=Op.PUSH0 * 5 + Op.STOP, max_stack_height=5),
                    ],
                    kind=ContainerKind.RUNTIME,
                ),
                "",
                EOFException.STACK_OVERFLOW,
                id="jumpf_stack_overflow_variable_stack_5",
            ),
                
        pytest.param(
                Container(
                    name="EOF1V00006",
                    sections=[
                        Section.Code(code=Op.PUSH0 + Op.PUSH1[0x00] + Op.RJUMPI[0x0002] + Op.PUSH0 * 1022 + Op.JUMPF[0x0001], max_stack_height=1023),
						Section.Code(code=Op.PUSH0 * 2 + Op.STOP, max_stack_height=2),
                    ],
                    kind=ContainerKind.RUNTIME,
                ),
                "",
                EOFException.STACK_OVERFLOW,
                id="jumpf_stack_overflow_variable_stack_6",
            ),
                
    ]
)

def test_example_valid_invalid(
    eof_test: EOFTestFiller,
    eof_code: Container,
    expected_hex_bytecode: str,
    exception: EOFException | None,
):
    """
    Verify eof container construction and exception
    """
    if expected_hex_bytecode[0:2] == "0x":
        expected_hex_bytecode = expected_hex_bytecode[2:]
    assert bytes(eof_code) == bytes.fromhex(expected_hex_bytecode)

    eof_test(
        data=eof_code,
        expect_exception=exception,
    )
